<style lang="scss">


  main {
    padding-top: 75px;
    padding-bottom: 56px;
  }

  .items {
    display: grid;
    grid-template-columns: 1fr 1fr;
    min-height: 100vh;
  }

  .item {
    background: #264653;
    display: grid;
    justify-content: center;
    color: #fff;
    grid-gap: 2vw;
    text-transform: uppercase;
    justify-items: center;
    font-size: 1rem;
    padding: 2vh;
    text-align: center;


    &:nth-child(2n) {
      background: #fff;
      color: #264653;
    }

    &:nth-child(3n) {
      background: #fff;
      color: #264653;
    }

    &:nth-child(4n) {
      background: #264653;
      color: #fff;

    }

    &:nth-child(5n) {
      grid-column: 1/-1;
      grid-auto-flow: column;
      justify-content: space-around;
    }

    &:nth-child(6n) {
      grid-column: 1/-1;
      grid-auto-flow: column;
      justify-content: space-around;

      .item__icon {
        grid-column: 2;
      }
    }

    @media screen and (min-width: 800px) {
      padding: 10vh;
      font-size: 2rem;

    }
  }

  .item__text {
    display: grid;
    align-content: center;
  }

  .item__icon {
    display: grid;
    align-content: center;

    svg {
      width: 90px;
    }
  }

  footer {
    background: #E76F51;
    display: grid;
    grid-auto-flow: column;
    align-content: center;
    height: 15vmin;
    justify-content: space-evenly;
    width: 100vw;
    z-index: 1;
    position: fixed;
    bottom: 0;
  }

</style>
